import React , { useState }from 'react';
import "./css/addProd.css";
import Cookies from 'js-cookie';
import DataTable from 'react-data-table-component';
import Axios from "axios";
const Inventario = () => {
    const [products, setProducts] = useState([]);
    var id_sesion = Cookies.get('session_UserID');
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.post('http://localhost:3001/api/users/getProducto', {
                    id:id_sesion
                });
                // Actualizar el estado de los productos con los datos obtenidos
                setProducts(response.data.data);
                // Terminar el estado de carga
               
            } catch (error) {
                console.error('Error al obtener los productos:', error);
            }
        };

        // Llamada a la función fetchProducts para obtener los datos
        fetchProducts();
    }, []); // El array vacío [] asegura que esto se ejecute una vez al montar el componente






    const columns = [
        { name: 'ID', selector: row => row.id, sortable: true },
        { name: 'Nombre', selector: row => row.nombre, sortable: true },
        { name: 'Precio', selector: row => row.precio, sortable: true },
        { name: 'Cantidad', selector: row => row.cantidad, sortable: true },
        { name: 'Descripción', selector: row => row.descrip, sortable: true },
        { name: 'Inventario', selector: row => row.inventario, sortable: true },
        { name: 'Categoria', selector: row => row.Categoria, sortable: true }
        // Agrega más columnas según sea necesario
    ];
    return( 
    <div className='producto'>
        <h2>Géstion de inventario</h2>
        <div className="users-table" >
            <DataTable
                    columns={columns}
                    data={products}
                    progressPending={loading}
                    pagination
                />
        </div>
    </div>
    );
};

export default Inventario;
