import "./Css/header.css";
import React, { useEffect, useState } from 'react';
import Axios from "axios";

const Cards_Ventas = () => {

  useEffect(() => {
    const fetchProducts = async () => {
        try {
            const response = await Axios.post('http://localhost:3001/api/users/getOneProduct', { id: id });
            const product = response.data.data;
            setNombre(product[0].nombre);
            setCategoria(product[0].categoria);
            setPrecio(product[0].precio);
            setCant(product[0].cantidad);
            setDesc(product[0].descrip);
            setInvent(product[0].inventario);
            setIsLoading(false);
        } catch (error) {
            console.error('Error al obtener los productos:', error);
        }
    };

    fetchProducts();
}, []);

  return (
    <div className="cartas">
        <h1>Ultimos productos</h1>
        <div className="container_cartas">
          <div className="card" >
            <img src="../Img/Ejemplos/camara.jpg" className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">Camara sony</h5>
              <p className="card-text">Sony ILCE-7M2K | Cuerpo ILC de fotograma Completo α7 II con Lente de Zoom estándar</p>
              <h2 className="card-text">$19,499</h2>
              <a href="#" className="btn btn-primary">Comprar</a>
            </div>
          </div>
          <div className="card" >
            <img src="../Img/Ejemplos/pantalla.jpg" className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">Camara sony</h5>
              <p className="card-text">Sony ILCE-7M2K | Cuerpo ILC de fotograma Completo α7 II con Lente de Zoom estándar</p>
              <h2 className="card-text">$19,499</h2>
              <a href="#" className="btn btn-primary">Comprar</a>
            </div>
          </div>
          <div className="card" >
            <img src="../Img/Ejemplos/laptop.jpg" className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">Camara sony</h5>
              <p className="card-text">Sony ILCE-7M2K | Cuerpo ILC de fotograma Completo α7 II con Lente de Zoom estándar</p>
              <h2 className="card-text">$19,499</h2>
              <a href="#" className="btn btn-primary">Comprar</a>
            </div>
          </div>
        </div>
      </div>
  );
}

export default Cards_Ventas;
