import "./Css/header.css";
import React, { useEffect, useState } from 'react';
import Axios from "axios";

const Cards_Ventas = () => {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    const fetchProducts = async () => {
      try {
          const response = await Axios.post('http://localhost:3001/api/users/getAllProducts', {
              
          });
          // Actualizar el estado de los productos con los datos obtenidos
          setProducts(response.data.data);
          // Terminar el estado de carga
         console.log(products[0]);
      } catch (error) {
          console.error('Error al obtener los productos:', error);
      }
    };

    fetchProducts();
}, []);
  const select = ()=>{
    Axios.post("http://localhost:3001/api/users/registerProduct",{
      id: sessionId,
      nombre:nombre,
      precio:precio,
      cantidad:cantidad,
      desc:desc,
      invent:invent
      
    }).then(response => {
      
      if(response.data.id==1){
        //en dado caso de que si se haya hecho con exito el registro
        console.log(response.data.id)
        alert(response.data.message);
        window.location.reload();//provicional, cambiar a history para uso completo de las funciones de ract
      }else{
        console.log(response.data.id)
        alert(response.data.message);
      }
      // Muestra el mensaje enviado desde el servidor
    }).catch(error=>{
      alert("Error con la comuniacion con el servidor");
    });
  }
  const clear = ()=>{
    window.location.reload();
  }
  return (
    <div className="cartas">
    <h1>Ultimos productos</h1>
    <div className="container_cartas">
      {products[0].map((product) => (
        <div className="card" key={product.productos_ID}>
          <img src={product.img} className="card-img-top" alt={product.nombre}/>
          <div className="card-body">
            <h5 className="card-title">{product.nombre}</h5>
            <p className="card-text">{product.descrip}</p>
            <h2 className="card-text">${product.precio}</h2>
            <a href="#" id={`${product.productos_ID}`} className="btn btn-primary" onClick={select}>Comprar</a>
          </div>
        </div>
      ))}
    </div>
  </div>
  );
}

export default Cards_Ventas;
